mapper.select:
SELECT count(ID) FROM ucm_resource a


		and a.ID = #{id}


		and a.CREATER = #{creater}


		and a.CREATER_TIME = #{createrTime}


		and a.DELETED = #{deleted}


		and a.DEPT_ID = #{deptId}


		and a.CODE  = #{code}


		and a.NAME  = #{name}


		and a.SYSTEM  = #{system}


		and a.MODULE  = #{module}


		and a.RESOURCE_TYPE  = #{resourceType}


		and a.ICONNAME  = #{iconname}


		and a.ORDER_NUM = #{orderNum}


		and a.LEVELS = #{levels}


		and a.PARENT_ID = #{parentId}

SELECT ID FROM ucm_resource a


		and a.ID = #{id}


		and a.CREATER = #{creater}


		and a.CREATER_TIME = #{createrTime}


		and a.DELETED = #{deleted}


		and a.DEPT_ID = #{deptId}


		and a.CODE  = #{code}


		and a.NAME  = #{name}


		and a.SYSTEM  = #{system}


		and a.MODULE  = #{module}


		and a.RESOURCE_TYPE  = #{resourceType}


		and a.ICONNAME  = #{iconname}


		and a.ORDER_NUM = #{orderNum}


		and a.LEVELS = #{levels}


		and a.PARENT_ID = #{parentId}




		${map.orderBy}

select
,ifnull (b.name,a.NAME ) as parent_name
from ucm_resource a   LEFT join ucm_resource b on a.parent_id = b.id


		and a.parent_id = #{map.parentId}


		and a.ID = #{id}


		and a.CREATER = #{creater}


		and a.CREATER_TIME = #{createrTime}


		and a.DELETED = #{deleted}


		and a.DEPT_ID = #{deptId}


		and a.CODE =#{code}


		and a.NAME like  concat('%',#{name},'%')


		and a.SYSTEM =#{system}


		and a.MODULE =#{module}


		and a.RESOURCE_TYPE =#{resourceType}


		and a.ICONNAME =#{iconname}


		and a.ORDER_NUM = #{orderNum}


		and a.LEVELS = #{levels}


		and a.PARENT_ID = #{parentId}




		${map.orderBy}


		ORDER BY a.ID asc

select

from ucm_resource a


		and a.ID = #{id}


		and a.CREATER = #{creater}


		and a.CREATER_TIME = #{createrTime}


		and a.DELETED = #{deleted}


		and a.DEPT_ID = #{deptId}


		and a.CODE =#{code}


		and a.NAME =#{name}


		and a.SYSTEM =#{system}


		and a.MODULE =#{module}


		and a.RESOURCE_TYPE =#{resourceType}


		and a.ICONNAME =#{iconname}


		and a.ORDER_NUM = #{orderNum}


		and a.LEVELS = #{levels}


		and a.PARENT_ID = #{parentId}


ORDER BY a.ID asc

mapper.delete:
delete from ucm_resource


		and ID = #{id}


		and CREATER = #{creater}


		and CREATER_TIME = #{createrTime}


		and DELETED = #{deleted}


		and DEPT_ID = #{deptId}


		and CODE =#{code}


		and NAME =#{name}


		and SYSTEM =#{system}


		and MODULE =#{module}


		and RESOURCE_TYPE =#{resourceType}


		and ICONNAME =#{iconname}


		and ORDER_NUM = #{orderNum}


		and LEVELS = #{levels}


		and PARENT_ID = #{parentId}
